# -*- coding: utf-8 -*-
from Crypto.Util.number import *
from gmpy2 import *
flag='flag{*************************}'
m=bytes_to_long(flag)

def F(n):
    if n == 1 or n == 2:
        return 1
    return F(n - 1) + F(n - 2)



for _ in range(3):
    p=getPrime(8)
    q=getPrime(1024)
    n=p*q
    F=F(n)
    c=pow(m,65537,F)
    print ('n = %d'%(n))
    print ('F(n) = %d'%(F))
    print ('c = %d'%(c))
    
